import React, { Component } from 'react'
import axios from "axios";

export default class Home extends Component {

    constructor(props) {
        super(props);
        this.state = {
            invoces: []
        }
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    componentDidMount() {
        fetch("http://localhost:3000/invoice")
            .then((response) => response.json())
            .then((json) => {
                this.setState({ invoces: json });
            });
    }

    handleSubmit(event) {


        event.preventDefault();
        axios
            .post("http://localhost:3000/invoice", {
                "description": "Une facture avec des produits",
                "paymentTerms": 7,
                "status": false,
                "total": 200,
                "itemList": [
                    "61bc4bd77acd735a0523cb52",
                    "2",
                    "6"
                ],
                "paymentDue": "2018-03-29T00:00:00.000Z",
            })
            .then((response) => {
                console.log(response);
            });
    };

    render() {
        return (
            <div id="signup">
                <form onSubmit={this.handleSubmit}>
                    <input placeholder="First Name" type="text" name="first_name" /><br />
                    <input placeholder="Last Name" type="text" name="last_name" /><br />
                    <button type="Submit">Start</button>
                </form>

            </div>
        )
    }
}
