import React, { Component } from 'react'

export default class Home extends Component {

    constructor(props) {
        super(props);
        this.state = {
            invoces: []
        }
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    componentDidMount() {
        let newInvoces = []
        fetch("http://localhost:3000/invoice")
            .then((response) => response.json())
            .then((json) => {
                this.setState({ invoces: json });
            });
    }

    handleSubmit(event) {
        event.preventDefault();
        fetch('http://localhost:3000/invoice', {
            method: 'post',
            headers: { 'Content-Type': 'application/json' },
            body: {
                "itemList": [
                    "61bc4bd77acd735a0523cb52",
                    "2",
                    "6"
                ],
                "description": "JE SUIS LA NOUVELLE FACTURE !",
                "paymentTerms": 7,
                "status": false,
                "total": 200,
                "create_date": "2021-12-17T08:32:16.565Z",
                "paymentDue": "2018-03-29T00:00:00.000Z",
                "__v": 0
            }
        });
    };

    render() {
        return (
            <div id="signup">
                <form onSubmit={this.handleSubmit}>
                    <input placeholder="First Name" type="text" name="first_name" /><br />
                    <input placeholder="Last Name" type="text" name="last_name" /><br />
                    <button type="Submit">Start</button>
                </form>

            </div>
        )
    }
}
