                        <input type="text" value={state.review} onChange={e => this.setState({ review: e.target.value })} />
                    </label>
                    <label>
                        Rating:
                        <input type="text" value={state.rating} onChange={e => this.setState({ rating: e.target.value })} />
                    </label>
                    <button type="Submit">Start</button>
                </form>

            </div>
        )
    }
}
