import React, { Component } from 'react'

export default class Home extends Component {

    constructor(props) {
        super(props);
        this.state = {
            invoces: []
        }
    }

    componentDidMount() {
        fetch("http://localhost:3000/invoice")
            .then((response) => response.json())
            .then((json) => {
                json.map((item) => {
                    let newInvoces = [...this.state.invoces, item]
                    this.setState({ invoces: newInvoces })
                }
                )

            });
    }

    render() {
        return (
            <div>
                {this.state.invoces}
            </div>
        )
    }
}
