import convert from './convert';
export { default as PDFDocument } from './document';
export * from './elements';
var pdfMake = require('pdfmake/build/pdfmake');
var pdfFonts = require('pdfmake/build/vfs_fonts');
pdfMake.vfs = pdfFonts.pdfMake.vfs;
export var pdf = function (content) {
    return pdfMake.createPdf(convert(content));
};
export var downloadPDF = function (content) {
    return pdf(content).download();
};
export var printPDF = function (content) {
    return pdf(content).print();
};
export var openPDF = function (content) {
    return pdf(content).open();
};
export default pdf;
